mapper.update:
UPDATE TB_RIDER
SET
	modified_datetime = now()


		, last_access = #{lastAccess}



		, name=#{name}



		, phone=#{phone}



		, code=#{code}



		, gender=#{gender}



		, employmentType=#{employmentType}



		, address = #{address}



		, working=#{working}



		, status=#{status}



		, vehicle_number=#{vehicleNumber}



		, emergency_phone=#{emergencyPhone}



		, workingHours=#{workingHours}



		, restHours=#{restHours}



		, teenager=#{teenager}



		, deleted=#{deleted}

WHERE 1=1



			AND admin_id = (SELECT admin_id FROM TB_STORE WHERE id = (SELECT store_id FROM TB_STORE_SESSION WHERE access_token = #{token}))


			AND admin_id = (SELECT admin_id FROM TB_ADMIN_SESSION WHERE access_token = #{token})

		AND id = #{id}


		AND id = (
		SELECT rider_id
		FROM TB_RIDER_SESSION
		WHERE access_token=#{accessToken}
		)

UPDATE TB_RIDER SET modified_datetime = now()
, working=#{working}
WHERE id = ( SELECT rider_id FROM TB_RIDER_SESSION WHERE access_token = #{accessToken} )

UPDATE TB_RIDER SET modified_datetime = now()
, latitude = #{latitude}
, longitude = #{longitude}
, location_updated = now() WHERE id = ( SELECT rider_id FROM TB_RIDER_SESSION WHERE access_token = #{accessToken})

mapper.select:
SELECT id , name , latitude , longitude , location_updated FROM TB_RIDER WHERE 1=1
AND id = #{id}
AND id = ( SELECT rider_id FROM TB_RIDER_SESSION WHERE access_token = #{accessToken})
