mapper.insert:

INSERT INTO TB_STORE_SESSION(store_id, access_token, created_datetime)
SELECT
	#{id}
	, #{accessToken}
	, now()
FROM TB_STORE
WHERE login_id = #{loginId}
AND   deleted is null

mapper.select:
select A.created_datetime ,A.modified_datetime ,A.last_access ,A.admin_id ,A.chat_user_id ,A.id ,A.login_id ,A.login_pw ,A.code ,A.name ,A.phone ,A.store_name ,A.ch_store_name ,A.store_phone ,A.address ,A.detail_address ,A.latitude ,A.longitude ,A.store_status ,A.store_distance_sort ,A.radius ,A.assignment_status ,A.assignment_limit ,A.third_party ,A.deleted ,A.alarm ,B.subgroup_id as subgroup_id ,B.group_id as subgroup_group_id ,( select name from TB_GROUP where admin_id = B.admin_id and id = B.group_id ) as subgroup_name ,B.admin_id as subgroup_admin_id ,( select name from TB_SUBGROUP where admin_id = B.admin_id and group_id = B.group_id and id = B.subgroup_id ) as group_name ,B.group_id as group_id ,B.admin_id as group_admin_id ,D.assignment_status as admin_assignment_status ,D.assignment_limit as admin_assignment_limit ,( select access_token from TB_STORE_SESSION where store_id = A.id ) as access_token ,ifnull(A.brand_code, '') as brand_code ,( select cd_value from TB_CODE_MASTER where title = 'brand_code' and used_flag = 1 and code = A.brand_code ) as brand_name ,concat('/resources/images/common/logo', A.brand_code, '.png') as brand_img from TB_STORE as A left join TB_SUBGROUP_STORE_REL as B on A.admin_id = B.admin_id and A.id = B.store_id and A.deleted is null and B.deleted is null ,TB_ADMIN as D where A.admin_id = D.id
AND A.id = #{id}
AND A.admin_id = (SELECT admin_id FROM TB_RIDER WHERE id = (SELECT rider_id FROM TB_RIDER_SESSION WHERE access_token = #{token}))
AND A.admin_id = (SELECT admin_id FROM TB_ADMIN_SESSION WHERE access_token = #{token})
AND A.id = #{id}
AND A.admin_id = (SELECT admin_id FROM TB_STORE WHERE id = (SELECT store_id FROM TB_STORE_SESSION WHERE access_token = #{token})) AND A.id = (SELECT store_id FROM TB_STORE_SESSION WHERE access_token=#{token} )

mapper.update:
		UPDATE TB_STORE AS A
		LEFT JOIN TB_SUBGROUP_STORE_REL AS B
		ON A.id = B.store_id
		LEFT JOIN TB_SUBGROUP_RIDER_REL AS C
		ON A.id = C.store_id
		SET
			A.modified_datetime = now()

				, A.expiration_date = #{expirationDate}



				, A.last_access = #{lastAccess}



				, A.code = #{code}



				, A.store_name = #{storeName}



				, A.ch_store_name = #{chStoreName}



				, A.store_phone = #{storePhone}



				, A.login_pw = #{loginPw}



				, A.address = #{address}



				, A.detail_address = #{detailAddress}



				, A.ch_address = #{chAddress}



				, A.ch_detail_address = #{chDetailAddress}



				, A.latitude = #{latitude}



				, A.longitude = #{longitude}



				, A.store_status = #{storeStatus}



				, A.radius = #{radius}



				, A.store_distance_sort = #{storeDistanceSort}


				, A.store_distance_sort = NULL



				, A.assignment_status = #{assignmentStatus}



				, A.assignment_limit = #{assignmentLimit}





                        , B.deleted = now()



                        , B.modified_datetime = now()
                        , B.store_id = #{id}
                        , B.subgroup_id = #{subGroupStoreRel.subGroupId}
                        , B.group_id = #{subGroupStoreRel.groupId}
                        , C.modified_datetime = now()
                        , C.store_id = #{id}
                        , C.subgroup_id = #{subGroupStoreRel.subGroupId}
                        , C.group_id = #{subGroupStoreRel.groupId}




		WHERE 1=1
            AND A.deleted IS NULL


                    AND A.admin_id = (SELECT admin_id FROM TB_ADMIN_SESSION WHERE access_token = #{token})
                    AND A.id = #{id}


                    AND A.admin_id = (SELECT * FROM (SELECT admin_id FROM TB_STORE WHERE id = (SELECT store_id FROM TB_STORE_SESSION WHERE access_token = #{token})) AS D)
                    AND A.id = (
                        SELECT store_id
                        FROM TB_STORE_SESSION
                        WHERE access_token=#{accessToken}
                    )

mapper.select:
select ( select store_name from TB_STORE where id = t.store_id ) as store_name ,sum( case when timestampdiff(MINUTE, newAssignedDatetime, picked_up_datetime) < 7 then 1 else 0 end ) as d7_success ,avg(time_to_sec(timediff(picked_up_datetime, newAssignedDatetime))) as order_pickup ,avg(time_to_sec(timediff(arrived_datetime, picked_up_datetime))) as pickup_complete ,avg(time_to_sec(timediff(arrived_datetime, newAssignedDatetime))) as order_complete ,avg(time_to_sec(timediff(return_datetime, arrived_datetime))) as complete_return ,avg(time_to_sec(timediff(return_datetime, picked_up_datetime))) as pickup_return ,avg(time_to_sec(timediff(return_datetime, newAssignedDatetime))) as order_return ,count( case when timestampdiff(MINUTE, newAssignedDatetime, picked_up_datetime) < 7 then 1 else null end ) / count(*) * 100 as min_D7below ,count( case when timestampdiff(MINUTE, newAssignedDatetime, arrived_datetime) <= 30 then 1 else null end ) / count(*) * 100 as min_30below ,count( case when timestampdiff(MINUTE, newAssignedDatetime, arrived_datetime) between 31 and 41 then 1 else null end ) / count(*) * 100 as min_30to40 ,count( case when timestampdiff(MINUTE, newAssignedDatetime, arrived_datetime) between 41 and 51 then 1 else null end ) / count(*) * 100 as min_40to50 ,count( case when timestampdiff(MINUTE, newAssignedDatetime, arrived_datetime) between 51 and 61 then 1 else null end ) / count(*) * 100 as min_50to60 ,count( case when timestampdiff(MINUTE, newAssignedDatetime, arrived_datetime) between 61 and 91 then 1 else null end ) / count(*) * 100 as min_60to90 ,count( case when timestampdiff(MINUTE, newAssignedDatetime, arrived_datetime) > 90 then 1 else null end ) / count(*) * 100 as min_90under -- ,count( case when condition1 = 1 or third_party_id is not null then null else 1 end ) as errtc -- 2021-12-07 배정 모드에 대한 집계 추가 ,count( case when assigned_type = 1 then 1 else null end ) as assigned_auto ,count( case when assigned_type = 2 then 1 else null end ) as assigned_manual ,count( case when third_party_id is not null then 1 else null end ) as thirdtc -- 2021-12-07 써드파티도 TC에 포함 ,count(*) as tc ,sum(total_price) as total_sales ,count(*) / (round(sum(timestampdiff(second, picked_up_datetime, return_datetime)) / 3600, 2)) as tplh ,sum(total_price) / (round(sum(timestampdiff(second, picked_up_datetime, return_datetime)) / 3600, 2)) as spmh ,sum(time_to_sec(timediff(return_datetime, picked_up_datetime))) as total_pickup_return ,avg(distance) as avg_distance from ( select store_id ,cooking_time ,reservation_datetime ,picked_up_datetime ,arrived_datetime ,completed_datetime ,return_datetime ,total_price ,distance ,third_party_id ,assigned_type ,if(timestampdiff(second, assigned_datetime, date_sub(reservation_datetime, interval case when ifnull(cooking_time, 30) > 30 or ifnull(cooking_time, 30) < 1 then 30 else ifnull(cooking_time, 30) end minute)) < 0, date_sub(reservation_datetime, interval case when ifnull(cooking_time, 30) > 30 or ifnull(cooking_time, 30) < 1 then 30 else ifnull(cooking_time, 30) end minute), assigned_datetime) as newAssignedDatetime FROM TB_ORDER WHERE status in (3) AND store_id = (SELECT store_id FROM TB_STORE_SESSION WHERE access_token = #{token}) AND date_add(#{currentDatetime}, interval (#{days} + 1) - 1 day) > created_datetime AND created_datetime > #{currentDatetime}
AND reservation_datetime >= #{currentDatetime} AND reservation_datetime < #{endDate}
AND reservation_datetime between #{currentDatetime} and date_add(#{currentDatetime}, interval (#{days} + 1) - 1 day)
AND date_format(reservation_datetime, '%H:%i:%s') between '11:30' and '13:30'
AND date_format(reservation_datetime, '%H:%i:%s') between '17:30' and '19:30'
AND ( (date_format(reservation_datetime, '%H:%i:%s') between '11:30' and '13:30') OR (date_format(reservation_datetime, '%H:%i:%s') between '17:30' and '19:30') )
AND created_datetime between #{currentDatetime} and date_add(#{currentDatetime}, interval (#{days} + 1) - 1 day)
) as t group by store_id
